con:configuration.scriptText:
//Verify the response time
assert messageExchange.timeTaken <500

//Verify the endpoint
//log.info messageExchange.getEndpoint()
def endpoint = "http://www.oorsprong.org/websamples.countryinfo/CountryInfoService.wso"
assert messageExchange.getEndpoint() == endpoint

//Verify header
///log.info (messageExchange.responseHeaders["Content-Length"])
assert messageExchange.responseHeaders["Content-Length"] != null

//Verify attachment
//log.info (messageExchange.responseAttachments.length)
assert (messageExchange.responseAttachments.length) ==0

//Verify the the respone nodes

def groovyUtils =new com.eviware.soapui.support.GroovyUtils( context )
def responseHolder = groovyUtils.getXmlHolder ( messageExchange.responseContent )
def CountryName = responseHolder.getNodeValue ("//m:CountryNameResult")
//log.info CountryName
assert CountryName == "India"

//Verify the the request nodes
def requestHolder = groovyUtils.getXmlHolder( messageExchange.requestContent )
def countryCode = requestHolder.getNodeValue ("//web:sCountryISOCode")
//log.info countryCode
assert countryCode == "IN"


//to get the xml response
def resp = messageExchange.responseContentAsXml.toString()
//log.info resp
